import SwiftUI

// MARK: - Text-only Modifiers
public extension Text {
    /// Tailwind-style text utilities \(font-size, font-weight, etc.\)
    func tw\(_ classes: String\) -> Text {
        var text = self
        let classList = classes.split\(separator: " "\).map\(String.init\)

        for className in classList {
            // Font sizes
            if className.hasPrefix\("text-"\) {
                if let size = parseTextSize\(className\) {
                    text = text.font\(.system\(size: size\)\)
                }
            }

            // Font weights
            switch className {
            case "font-bold": text = text.fontWeight\(.bold\)
            case "font-semibold": text = text.fontWeight\(.semibold\)
            case "font-medium": text = text.fontWeight\(.medium\)
            case "font-light": text = text.fontWeight\(.light\)
            case "font-thin": text = text.fontWeight\(.thin\)
            case "italic": text = text.italic\(\)
            default: break
            }

            // Text case
            switch className {
            case "uppercase": text = text.textCase\(.uppercase\)
            case "lowercase": text = text.textCase\(.lowercase\)
            case "capitalize": text = text.textCase\(nil\)
            default: break
            }
        }

        return text
    }

    private func parseTextSize\(_ className: String\) -> CGFloat? {
        switch className {
        case "text-xs": return 12
        case "text-sm": return 14
        case "text-base": return 16
        case "text-lg": return 18
        case "text-xl": return 20
        case "text-2xl": return 24
        case "text-3xl": return 30
        case "text-4xl": return 36
        case "text-5xl": return 48
        case "text-6xl": return 60
        case "text-7xl": return 72
        case "text-8xl": return 96
        case "text-9xl": return 128
        default: return nil
        }
    }
}
