import Foundation

// Generate complete color parser mappings from Colors.swift
let colors = ["slate", "gray", "zinc", "neutral", "stone", "red", "orange", "amber", 
              "yellow", "lime", "green", "emerald", "teal", "cyan", "sky", "blue", 
              "indigo", "violet", "purple", "fuchsia", "pink", "rose"]
let shades = ["50", "100", "200", "300", "400", "500", "600", "700", "800", "900", "950"]

print\("// Generated color mappings"\)
print\("switch \\"\\\\\(name\)\\\\\(shade\)\\" {"\)

for color in colors {
    for shade in shades {
        print\("case \\"\\\(color\)\\\(shade\)\\": return .\\\(color\)\\\(shade\)"\)
    }
}

print\("default: return nil"\)
print\("}"\)
